  1→import SwiftUI
  2→import WebKit
  3→
  4→public struct MarkdownWebView: NSViewRepresentable {
  5→    public let markdownContent: String
  6→    public let baseURL: URL?
  7→
  8→    public init(markdownContent: String, baseURL: URL? = nil) {
  9→        self.markdownContent = markdownContent
 10→        self.baseURL = baseURL
 11→    }
 12→
 13→    public func makeCoordinator() -> Coordinator {
 14→        Coordinator()
 15→    }
 16→
 17→    public func makeNSView(context: Context) -> WKWebView {
 18→        let config = WKWebViewConfiguration()
 19→        let prefs = WKWebpagePreferences()
 20→        prefs.allowsContentJavaScript = true
 21→        config.defaultWebpagePreferences = prefs
 22→
 23→        let webView = WKWebView(frame: .zero, configuration: config)
 24→        webView.setValue(false, forKey: "drawsBackground")
 25→        webView.navigationDelegate = context.coordinator
 26→
 27→        context.coordinator.webView = webView
 28→        context.coordinator.pendingContent = markdownContent
 29→        context.coordinator.baseURL = baseURL
 30→        loadTemplate(into: webView, baseURL: baseURL)
 31→
 32→        return webView
 33→    }
 34→
 35→    public func updateNSView(_ webView: WKWebView, context: Context) {
 36→        // Update base URL if changed
 37→        if context.coordinator.baseURL != baseURL {
 38→            context.coordinator.baseURL = baseURL
 39→            loadTemplate(into: webView, baseURL: baseURL)
 40→        }
 41→
 42→        if context.coordinator.isLoaded {
 43→            evaluateRender(webView: webView, content: markdownContent, baseURL: baseURL)
 44→        } else {
 45→            context.coordinator.pendingContent = markdownContent
 46→        }
 47→    }
 48→
 49→    private func loadTemplate(into webView: WKWebView, baseURL: URL?) {
 50→        AppLogger.shared.log("loadTemplate called, baseURL: \(baseURL?.path ?? "nil")")
 51→
 52→        // Find template.html - check app bundle locations first to avoid triggering
 53→        // Bundle.module's resource_bundle_accessor which crashes in packaged apps
 54→        let possibleURLs: [URL?] = [
 55→            // App bundle: MDPreview_MDPreviewCore.bundle/Resources/template.html
 56→            Bundle.main.url(forResource: "MDPreview_MDPreviewCore", withExtension: "bundle")?
 57→                .appendingPathComponent("Resources/template.html"),
 58→            // Direct in Resources folder
 59→            Bundle.main.resourceURL?.appendingPathComponent("MDPreview_MDPreviewCore.bundle/Resources/template.html"),
 60→            // Fallback: try root Resources
 61→            Bundle.main.url(forResource: "template", withExtension: "html"),
 62→            // Last resort: Bundle.module (only works in SPM builds/tests)
 63→            safeModuleURL(forResource: "template", withExtension: "html", subdirectory: "Resources")
 64→        ]
 65→
 66→        // Log all possible paths for debugging
 67→        for (index, url) in possibleURLs.enumerated() {
 68→            guard let url = url else {
 69→                AppLogger.shared.log("Path \(index): nil")
 70→                continue
 71→            }
 72→            let exists = FileManager.default.fileExists(atPath: url.path)
 73→            AppLogger.shared.log("Path \(index): \(url.path), exists: \(exists)")
 74→        }
 75→
 76→        guard let resourceURL = possibleURLs.compactMap({ $0 }).first(where: { FileManager.default.fileExists(atPath: $0.path) }) else {
 77→            AppLogger.shared.log("ERROR: No template.html found!")
 78→            return
 79→        }
 80→
 81→        AppLogger.shared.log("Using template: \(resourceURL.path)")
 82→
 83→        // Use the markdown file's directory as base URL for resolving relative paths
 84→        let readAccessURL: URL
 85→        if let base = baseURL {
 86→            readAccessURL = base
 87→        } else {
 88→            readAccessURL = resourceURL.deletingLastPathComponent()
 89→        }
 90→
 91→        webView.loadFileURL(resourceURL, allowingReadAccessTo: readAccessURL)
 92→    }
 93→
 94→    /// Safely access Bundle.module without crashing in packaged apps
 95→    private func safeModuleURL(forResource name: String, withExtension ext: String?, subdirectory: String?) -> URL? {
 96→        // Only use Bundle.module if we're not in a packaged app
 97→        // Check if Bundle.main has an Info.plist (indicates packaged app)
 98→        guard Bundle.main.infoDictionary != nil,
 99→              Bundle.main.bundleIdentifier != nil else {
100→            // Not a packaged app, safe to use Bundle.module
101→            return Bundle.module.url(forResource: name, withExtension: ext, subdirectory: subdirectory)
102→        }
103→
104→        // In packaged app, try to find the module bundle manually
105→        let moduleName = "MDPreview_MDPreviewCore"
106→        if let bundleURL = Bundle.main.url(forResource: moduleName, withExtension: "bundle") {
107→            if let bundle = Bundle(url: bundleURL) {
108→                return bundle.url(forResource: name, withExtension: ext, subdirectory: subdirectory)
109→            }
110→        }
111→        return nil
112→    }
113→
114→    private func evaluateRender(webView: WKWebView, content: String, baseURL: URL? = nil) {
115→        var processedContent = content
116→
117→        // Convert relative image paths to absolute file URLs
118→        if let base = baseURL {
119→            processedContent = Self.rewriteRelativePaths(in: content, baseURL: base)
120→        }
121→
122→        let escaped = MarkdownWebView.escapeForJavaScript(processedContent)
123→        let basePath = baseURL?.path ?? ""
124→        webView.evaluateJavaScript("render(`\(escaped)`, `\(basePath)`)")
125→    }
126→
127→    /// Rewrite relative image and link paths to absolute file URLs
128→    private static func rewriteRelativePaths(in markdown: String, baseURL: URL) -> String {
129→        let baseDirectory = baseURL.path
130→
131→        // Pattern to match markdown images and links with relative paths
132→        // Matches: ![](./image.png), ![](screenshots/img.png), [](./file.md)
133→        // Does NOT match: ![](http://...), ![](https://...), ![](#anchor)
134→        let relativePattern = #"(!?\[[^\]]*\]\()(\.\/[^)]+|[^)#:\s][^)]*[^\)#:\s])(\))"#
135→
136→        var result = markdown
137→
138→        // Use NSDataDetector-like approach with regex
139→        if let regex = try? NSRegularExpression(pattern: relativePattern, options: []) {
140→            let range = NSRange(markdown.startIndex..., in: markdown)
141→            let matches = regex.matches(in: markdown, options: [], range: range)
142→
143→            // Process matches in reverse order to maintain correct indices
144→            for match in matches.reversed() {
145→                guard match.numberOfRanges >= 3,
146→                      let prefixRange = Range(match.range(at: 1), in: markdown),
147→                      let pathRange = Range(match.range(at: 2), in: markdown) else {
148→                    continue
149→                }
150→
151→                let prefix = String(markdown[prefixRange])  // ![]( or [](
152→                var relativePath = String(markdown[pathRange])  // ./image.png or screenshots/img.png
153→
154→                // Skip if already an absolute URL or anchor
155→                if relativePath.hasPrefix("http://") ||
156→                   relativePath.hasPrefix("https://") ||
157→                   relativePath.hasPrefix("file://") ||
158→                   relativePath.hasPrefix("#") ||
159→                   relativePath.hasPrefix("/") ||
160→                   relativePath.hasPrefix("data:") {
161→                    continue
162→                }
163→
164→                // Remove leading ./
165→                if relativePath.hasPrefix("./") {
166→                    relativePath = String(relativePath.dropFirst(2))
167→                }
168→
169→                // Construct absolute file URL
170→                let absolutePath = "file://\(baseDirectory)/\(relativePath)"
171→
172→                // Replace in result
173→                if let fullMatchRange = Range(match.range, in: result) {
174→                    let replacement = "\(prefix)\(absolutePath))"
175→                    result.replaceSubrange(fullMatchRange, with: replacement)
176→                }
177→            }
178→        }
179→
180→        return result
181→    }
182→
183→    /// Escape a string for safe embedding in a JavaScript template literal.
184→    /// Exposed as static for testing.
185→    public static func escapeForJavaScript(_ string: String) -> String {
186→        string
187→            .replacingOccurrences(of: "\\", with: "\\\\")
188→            .replacingOccurrences(of: "`", with: "\\`")
189→            .replacingOccurrences(of: "$", with: "\\$")
190→            .replacingOccurrences(of: "\r\n", with: "\\n")
191→            .replacingOccurrences(of: "\r", with: "\\n")
192→    }
193→
194→    public final class Coordinator: NSObject, WKNavigationDelegate {
195→        public var webView: WKWebView?
196→        public var isLoaded = false
197→        public var pendingContent: String?
198→        public var baseURL: URL?
199→        private var scrollObserver: NSObjectProtocol?
200→
201→        override init() {
202→            super.init()
203→            // Listen for scroll to anchor notifications
204→            scrollObserver = NotificationCenter.default.addObserver(
205→                forName: .scrollToAnchor,
206→                object: nil,
207→                queue: .main
208→            ) { [weak self] notification in
209→                guard let anchor = notification.object as? String else { return }
210→                self?.scrollToAnchor(anchor)
211→            }
212→        }
213→
214→        deinit {
215→            if let observer = scrollObserver {
216→                NotificationCenter.default.removeObserver(observer)
217→            }
218→        }
219→
220→        private func scrollToAnchor(_ anchor: String) {
221→            guard let webView = webView else { return }
222→            let escapedAnchor = anchor.addingPercentEncoding(withAllowedCharacters: .alphanumerics) ?? anchor
223→            webView.evaluateJavaScript("scrollToAnchor('\(escapedAnchor)')")
224→        }
225→
226→        public func webView(_ webView: WKWebView, didFinish navigation: WKNavigation!) {
227→            AppLogger.shared.log("WebView finished loading")
228→            isLoaded = true
229→            if let content = pendingContent {
230→                pendingContent = nil
231→                // Convert relative paths to absolute file URLs
232→                var processedContent = content
233→                if let base = baseURL {
234→                    processedContent = MarkdownWebView.rewriteRelativePaths(in: content, baseURL: base)
235→                }
236→                let escaped = MarkdownWebView.escapeForJavaScript(processedContent)
237→                let basePath = baseURL?.path ?? ""
238→                webView.evaluateJavaScript("render(`\(escaped)`, `\(basePath)`)")
239→            }
240→        }
241→
242→        public func webView(_ webView: WKWebView, didFail navigation: WKNavigation!, withError error: Error) {
243→            AppLogger.shared.log("WebView navigation failed: \(error.localizedDescription)")
244→        }
245→
246→        public func webView(_ webView: WKWebView, didFailProvisionalNavigation navigation: WKNavigation!, withError error: Error) {
247→            AppLogger.shared.log("WebView provisional navigation failed: \(error.localizedDescription)")
248→        }
249→
250→        public func webView(
251→            _ webView: WKWebView,
252→            decidePolicyFor navigationAction: WKNavigationAction,
253→            decisionHandler: @escaping (WKNavigationActionPolicy) -> Void
254→        ) {
255→            // Handle link clicks
256→            if navigationAction.navigationType == .linkActivated,
257→               let url = navigationAction.request.url {
258→
259→                // Markdown files: open in app (new tab)
260→                if url.isFileURL && isMarkdownFile(url) {
261→                    NotificationCenter.default.post(name: .didRequestOpenFile, object: url)
262→                    decisionHandler(.cancel)
263→                    return
264→                }
265→
266→                // Other local file URLs (images, etc.): allow in webview
267→                if url.isFileURL {
268→                    decisionHandler(.allow)
269→                    return
270→                }
271→
272→                // External links: open in default browser
273→                NSWorkspace.shared.open(url)
274→                decisionHandler(.cancel)
275→                return
276→            }
277→            decisionHandler(.allow)
278→        }
279→
280→        private func isMarkdownFile(_ url: URL) -> Bool {
281→            let ext = url.pathExtension.lowercased()
282→            return ext == "md" || ext == "markdown" || ext == "mdown" || ext == "mkd"
283→        }
284→    }
285→}
