  1→import SwiftUI
  2→import UniformTypeIdentifiers
  3→
  4→public struct MDPreviewApp: App {
  5→    @NSApplicationDelegateAdaptor(AppDelegate.self) var appDelegate
  6→    @StateObject private var workspace = Workspace()
  7→
  8→    public init() {}
  9→
 10→    public var body: some Scene {
 11→        WindowGroup {
 12→            MainView(workspace: workspace)
 13→                .frame(minWidth: 500, minHeight: 400)
 14→                .background(Color(nsColor: .textBackgroundColor))
 15→                .onAppear {
 16→                    openFromCommandLineArgs()
 17→                    restoreStateIfNeeded()
 18→                }
 19→                .onReceive(NotificationCenter.default.publisher(for: .didRequestOpenFile)) { notification in
 20→                    if let url = notification.object as? URL {
 21→                        workspace.openURL(url)
 22→                    }
 23→                }
 24→        }
 25→        .commands {
 26→            // MARK: - File Menu
 27→            CommandGroup(replacing: .newItem) {
 28→                Button("Open...") {
 29→                    workspace.showOpenPanel()
 30→                }
 31→                .keyboardShortcut("o", modifiers: .command)
 32→            }
 33→
 34→            CommandGroup(after: .saveItem) {
 35→                Button("Export Logs...") {
 36→                    exportLogs()
 37→                }
 38→            }
 39→
 40→            CommandGroup(after: .appSettings) {
 41→                Button("Install Command Line Tool...") {
 42→                    CLIInstaller.install()
 43→                }
 44→            }
 45→
 46→            // MARK: - View Menu (extend default View menu)
 47→            CommandGroup(after: .toolbar) {
 48→                Button("Toggle Sidebar") {
 49→                    workspace.toggleSidebar()
 50→                }
 51→                .keyboardShortcut("s", modifiers: [.command, .control])
 52→
 53→                Button("Toggle Table of Contents") {
 54→                    NotificationCenter.default.post(name: .toggleTOC, object: nil)
 55→                }
 56→                .keyboardShortcut("t", modifiers: [.command, .control])
 57→
 58→                Divider()
 59→
 60→                Toggle("Show Hidden Files", isOn: $workspace.showHiddenFiles)
 61→                    .keyboardShortcut(".", modifiers: [.command, .shift])
 62→            }
 63→
 64→            // MARK: - Window Menu
 65→            CommandGroup(replacing: .windowArrangement) {
 66→                Button("Close Tab") {
 67→                    workspace.closeCurrentTab()
 68→                }
 69→                .keyboardShortcut("w", modifiers: .command)
 70→
 71→                Button("Close All Tabs") {
 72→                    workspace.closeAllTabs()
 73→                }
 74→                .keyboardShortcut("w", modifiers: [.command, .option])
 75→
 76→                Divider()
 77→
 78→                Button("Next Tab") {
 79→                    workspace.selectNextTab()
 80→                }
 81→                .keyboardShortcut("]", modifiers: [.command, .shift])
 82→
 83→                Button("Previous Tab") {
 84→                    workspace.selectPreviousTab()
 85→                }
 86→                .keyboardShortcut("[", modifiers: [.command, .shift])
 87→
 88→                Divider()
 89→
 90→                // Cmd+1 through Cmd+9 for tab access
 91→                ForEach(1...9, id: \.self) { index in
 92→                    Button("Tab \(index)") {
 93→                        workspace.selectTab(at: index - 1)
 94→                    }
 95→                    .keyboardShortcut(KeyEquivalent(Character("\(index)")), modifiers: .command)
 96→                }
 97→            }
 98→
 99→            // MARK: - Help Menu
100→            CommandGroup(replacing: .help) {
101→                Button("MDPreview Website") {
102→                    if let url = URL(string: "https://github.com/kazu-42/mdpreview") {
103→                        NSWorkspace.shared.open(url)
104→                    }
105→                }
106→
107→                Button("Report an Issue") {
108→                    if let url = URL(string: "https://github.com/kazu-42/mdpreview/issues") {
109→                        NSWorkspace.shared.open(url)
110→                    }
111→                }
112→            }
113→        }
114→        .defaultSize(width: 900, height: 700)
115→    }
116→
117→    private func openFromCommandLineArgs() {
118→        let args = CommandLine.arguments.dropFirst()
119→        for arg in args {
120→            guard !arg.hasPrefix("-") else { continue }
121→            workspace.openFromPath(arg)
122→        }
123→    }
124→
125→    private func restoreStateIfNeeded() {
126→        // Only restore if no files were opened via CLI
127→        if workspace.tabs.isEmpty {
128→            workspace.restoreState()
129→        }
130→    }
131→
132→    private func exportLogs() {
133→        let panel = NSSavePanel()
134→        panel.title = "Export Logs"
135→        let formatter = DateFormatter()
136→        formatter.dateFormat = "yyyy-MM-dd_HHmmss"
137→        panel.nameFieldStringValue = "mdpreview-\(formatter.string(from: Date())).log"
138→        panel.allowedContentTypes = [UTType(filenameExtension: "log") ?? .plainText]
139→
140→        if panel.runModal() == .OK, let url = panel.url {
141→            do {
142→                try AppLogger.shared.exportLogs(to: url)
143→            } catch {
144→                workspace.errorMessage = "Failed to export logs: \(error.localizedDescription)"
145→            }
146→        }
147→    }
148→}
