 1→import AppKit
 2→
 3→public extension Notification.Name {
 4→    static let didRequestOpenFile = Notification.Name("didRequestOpenFile")
 5→}
 6→
 7→public final class AppDelegate: NSObject, NSApplicationDelegate {
 8→    public override init() {
 9→        super.init()
10→    }
11→
12→    public func applicationDidFinishLaunching(_ notification: Notification) {
13→        NSApp.setActivationPolicy(.regular)
14→        NSApp.activate(ignoringOtherApps: true)
15→    }
16→
17→    public func application(_ application: NSApplication, open urls: [URL]) {
18→        for url in urls {
19→            NotificationCenter.default.post(name: .didRequestOpenFile, object: url)
20→        }
21→        NSApp.activate(ignoringOtherApps: true)
22→    }
23→
24→    public func applicationShouldHandleReopen(_ sender: NSApplication, hasVisibleWindows flag: Bool) -> Bool {
25→        if !flag {
26→            return true
27→        }
28→        NSApp.activate(ignoringOtherApps: true)
29→        return false
30→    }
31→
32→    public func applicationShouldTerminateAfterLastWindowClosed(_ sender: NSApplication) -> Bool {
33→        true
34→    }
35→}
