160→<body>
161→    <div id="content"></div>
162→    <script>
163→        marked.use({
164→            gfm: true,
165→            breaks: false
166→        });
167→
168→        function render(markdown, basePath) {
169→            // Set base URL for relative path resolution (images, links)
170→            if (basePath) {
171→                var base = document.querySelector('base');
172→                if (!base) {
173→                    base = document.createElement('base');
174→                    document.head.appendChild(base);
175→                }
176→                base.href = 'file://' + basePath + '/';
177→            }
178→
179→            // Save scroll position
