 3→public final class FileWatcher {
 4→    private var source: DispatchSourceFileSystemObject?
 5→    private var fileDescriptor: Int32 = -1
 6→    private var debounceTimer: DispatchSourceTimer?
 7→    public let debounceInterval: TimeInterval
 8→
 9→    public init(debounceInterval: TimeInterval = 0.15) {
10→        self.debounceInterval = debounceInterval
11→    }
12→
13→    public func watch(url: URL, onChange: @escaping () -> Void) {
14→        stop()
15→
16→        let path = url.path
17→        fileDescriptor = open(path, O_EVTONLY)
18→        guard fileDescriptor >= 0 else { return }
19→
20→        let source = DispatchSource.makeFileSystemObjectSource(
21→            fileDescriptor: fileDescriptor,
22→            eventMask: [.write, .delete, .rename, .attrib],
23→            queue: .main
24→        )
25→
26→        source.setEventHandler { [weak self] in
27→            guard let self else { return }
28→            let flags = source.data
29→            if flags.contains(.delete) || flags.contains(.rename) {
30→                // File was replaced (editors often write-then-rename)
31→                DispatchQueue.main.asyncAfter(deadline: .now() + 0.05) {
32→                    self.watch(url: url, onChange: onChange)
33→                    self.debouncedOnChange(onChange)
34→                }
35→                return
36→            }
37→            self.debouncedOnChange(onChange)
38→        }
39→
40→        source.setCancelHandler { [fd = fileDescriptor] in
41→            close(fd)
42→        }
43→
44→        self.source = source
45→        source.resume()
46→    }
47→
48→    public var isWatching: Bool {
49→        source != nil
50→    }
51→
52→    public func stop() {
53→        debounceTimer?.cancel()
54→        debounceTimer = nil
55→        source?.cancel()
56→        source = nil
57→        fileDescriptor = -1
58→    }
59→
60→    private func debouncedOnChange(_ onChange: @escaping () -> Void) {
61→        debounceTimer?.cancel()
62→        let timer = DispatchSource.makeTimerSource(queue: .main)
63→        timer.schedule(deadline: .now() + debounceInterval)
64→        timer.setEventHandler {
65→            onChange()
66→        }
67→        debounceTimer = timer
68→        timer.resume()
69→    }
70→
71→    deinit {
72→        stop()
73→    }
74→}
