  4→
  5→public struct TabItem: Identifiable, Equatable {
  6→    public let id = UUID()
  7→    public let url: URL
  8→
  9→    public var name: String { url.lastPathComponent }
 10→    public var directoryURL: URL { url.deletingLastPathComponent() }
 11→
 12→    public static func == (lhs: TabItem, rhs: TabItem) -> Bool {
 13→        lhs.id == rhs.id
 14→    }
 15→}
 16→
 17→public final class Workspace: ObservableObject {
 18→    // MARK: - Published State
 19→
 20→    @Published public var tabs: [TabItem] = []
 21→    @Published public var selectedTabID: UUID?
 22→    @Published public var directoryURL: URL?
 23→    @Published public var fileTreeNodes: [FileTreeNode] = []
 24→    @Published public var markdownContent: String = "" {
 25→        didSet {
 26→            tocItems = extractTOC(from: markdownContent)
 27→        }
 28→    }
 29→    @Published public var tocItems: [TOCItem] = []
 30→    @Published public var errorMessage: String?
 31→
 32→    // Persisted state
 33→    @AppStorage("sidebarWidth") public var sidebarWidth: Double = 200
 34→    @AppStorage("lastOpenedFiles") private var lastOpenedFilesData: Data = Data()
 35→    @AppStorage("lastDirectory") private var lastDirectoryPath: String = ""
 36→    @AppStorage("showHiddenFiles") private var storedShowHiddenFiles: Bool = true
 37→
 38→    /// Whether to show hidden files in the file tree
 39→    @Published public var showHiddenFiles: Bool = true {
 40→        didSet { refreshFileTree() }
 41→    }
 42→
 43→    /// Computed property for showing error alerts
 44→    public var showError: Bool {
 45→        get { errorMessage != nil }
 46→        set { if !newValue { errorMessage = nil } }
 47→    }
 48→
 49→    /// Current file's directory for resolving relative image paths
 50→    public var currentFileDirectory: URL? {
 51→        selectedTab?.directoryURL ?? directoryURL
 52→    }
 53→
 54→    private let fileWatcher = FileWatcher()
 55→
 56→    public init() {
 57→        showHiddenFiles = storedShowHiddenFiles
 58→    }
 59→
 60→    // MARK: - Computed Properties
 61→
 62→    public var selectedTab: TabItem? {
 63→        guard let id = selectedTabID else { return nil }
 64→        return tabs.first { $0.id == id }
 65→    }
 66→
 67→    public var displayName: String {
 68→        if let tab = selectedTab {
 69→            if let dir = directoryURL {
 70→                return "\(tab.name) — \(dir.lastPathComponent)"
 71→            }
 72→            return tab.name
 73→        }
 74→        if let dir = directoryURL {
 75→            return dir.lastPathComponent
 76→        }
 77→        return "MDPreview"
 78→    }
 79→
 80→    // MARK: - Opening
 81→
 82→    public func openFile(_ url: URL) {
 83→        let standardized = url.standardizedFileURL
 84→
 85→        // Check if file is readable
 86→        guard FileManager.default.isReadableFile(atPath: standardized.path) else {
 87→            errorMessage = "Cannot read file: \(standardized.lastPathComponent)"
 88→            return
 89→        }
 90→
 91→        if let existing = tabs.first(where: { $0.url.standardizedFileURL == standardized }) {
 92→            selectTab(existing.id)
 93→            return
 94→        }
 95→        let tab = TabItem(url: standardized)
 96→        tabs.append(tab)
 97→        selectTab(tab.id)
 98→        saveState()
 99→    }
100→
101→    public func openDirectory(_ url: URL) {
102→        directoryURL = url.standardizedFileURL
103→        fileTreeNodes = FileTreeNode.buildTree(from: url, showHidden: showHiddenFiles)
104→        lastDirectoryPath = url.path
105→    }
106→
107→    public func openURL(_ url: URL) {
108→        var isDir: ObjCBool = false
109→        guard FileManager.default.fileExists(atPath: url.path, isDirectory: &isDir) else {
110→            errorMessage = "File not found: \(url.lastPathComponent)"
111→            return
112→        }
113→        if isDir.boolValue {
114→            openDirectory(url)
115→        } else {
116→            openFile(url)
117→        }
118→    }
119→
120→    public func openFromPath(_ path: String) {
121→        let url: URL
122→        if path.hasPrefix("/") || path.hasPrefix("~") {
123→            url = URL(fileURLWithPath: (path as NSString).expandingTildeInPath)
124→        } else {
125→            let cwd = FileManager.default.currentDirectoryPath
126→            url = URL(fileURLWithPath: cwd).appendingPathComponent(path)
127→        }
128→        openURL(url)
129→    }
130→
131→    // MARK: - Tabs
132→
133→    public func selectTab(_ id: UUID) {
134→        guard let tab = tabs.first(where: { $0.id == id }) else { return }
135→        selectedTabID = tab.id
136→        loadContent(for: tab)
137→    }
138→
139→    public func selectTab(at index: Int) {
140→        guard index >= 0, index < tabs.count else { return }
141→        selectTab(tabs[index].id)
142→    }
143→
144→    public func closeTab(_ id: UUID) {
145→        guard let index = tabs.firstIndex(where: { $0.id == id }) else { return }
146→        let wasSelected = selectedTabID == id
147→        tabs.remove(at: index)
148→
149→        if wasSelected {
150→            if tabs.isEmpty {
151→                selectedTabID = nil
152→                markdownContent = ""
153→                fileWatcher.stop()
154→            } else {
155→                let newIndex = min(index, tabs.count - 1)
156→                selectTab(tabs[newIndex].id)
157→            }
158→        }
159→        saveState()
160→    }
161→
162→    public func closeCurrentTab() {
163→        guard let id = selectedTabID else {
164→            // No tabs, close window
165→            NSApp.keyWindow?.close()
166→            return
167→        }
168→        closeTab(id)
169→        if tabs.isEmpty && directoryURL == nil {
170→            NSApp.keyWindow?.close()
171→        }
172→    }
173→
174→    public func closeAllTabs() {
175→        tabs.removeAll()
176→        selectedTabID = nil
177→        markdownContent = ""
178→        fileWatcher.stop()
179→        saveState()
180→    }
181→
182→    public func selectNextTab() {
