import AppKit
import MDPreviewCore

let app = NSApplication.shared

// Quick Look mode: CLI launch with a file argument
let hasFileArg = CommandLine.arguments.count > 1
    && !CommandLine.arguments[1].hasPrefix("-")

if hasFileArg {
    QuickLookMode.isEnabled = true
    app.setActivationPolicy(.accessory)
} else {
    app.setActivationPolicy(.regular)
}

app.activate(ignoringOtherApps: true)

MDPreviewApp.main()

