import AppKit

public extension Notification.Name {
    static let didRequestOpenFile = Notification.Name("didRequestOpenFile")
}

public final class AppDelegate: NSObject, NSApplicationDelegate, NSWindowDelegate {
    private var eventMonitor: Any?

    public override init() {
        super.init()
    }

    public func applicationDidFinishLaunching(_ notification: Notification) {
        if QuickLookMode.isEnabled {
            NSApp.setActivationPolicy(.accessory)
            setupQuickLookWindow()
            setupKeyHandler()
        } else {
            NSApp.setActivationPolicy(.regular)
        }
        NSApp.activate(ignoringOtherApps: true)
    }

    public func application(_ application: NSApplication, open urls: [URL]) {
        guard let url = urls.first else { return }
        NotificationCenter.default.post(name: .didRequestOpenFile, object: url)
    }

    // MARK: - Quick Look Mode

    private func setupQuickLookWindow() {
        DispatchQueue.main.async {
            guard let window = NSApp.windows.first else { return }
            window.level = .floating
            window.collectionBehavior = [.canJoinAllSpaces, .fullScreenAuxiliary]
            window.center()
            window.delegate = self
        }
    }

    private func setupKeyHandler() {
        eventMonitor = NSEvent.addLocalMonitorForEvents(matching: .keyDown) { event in
            switch event.keyCode {
            case 49, 53:  // Space, Escape
                NSApp.terminate(nil)
                return nil
            case 12:  // Q key
                if event.modifierFlags.contains(.command) {
                    NSApp.terminate(nil)
                    return nil
                }
                return event
            default:
                return event
            }
        }
    }

    // MARK: - NSWindowDelegate

    public func windowWillClose(_ notification: Notification) {
        if QuickLookMode.isEnabled {
            NSApp.terminate(nil)
        }
    }
}

