private func setupQuickLookWindow() {
    // SwiftUI window may not exist yet; poll briefly until it appears
    func configure() {
        guard let window = NSApp.windows.first(where: { $0.contentView != nil }) else {
            DispatchQueue.main.asyncAfter(deadline: .now() + 0.05) { configure() }
            return
        }
        window.level = .floating
        window.collectionBehavior = [.canJoinAllSpaces, .fullScreenAuxiliary]
        window.center()
        window.delegate = self
        // Force window to front even with .accessory policy
        window.makeKeyAndOrderFront(nil)
        NSApp.activate(ignoringOtherApps: true)
    }
    DispatchQueue.main.async { configure() }
}

