     1→import SwiftUI
     2→import AppKit
     3→import UniformTypeIdentifiers
     4→
     5→public struct TabItem: Identifiable, Equatable {
     6→    public let id = UUID()
     7→    public let url: URL
     8→
     9→    public var name: String { url.lastPathComponent }
    10→    public var directoryURL: URL { url.deletingLastPathComponent() }
    11→
    12→    public static func == (lhs: TabItem, rhs: TabItem) -> Bool {
    13→        lhs.id == rhs.id
    14→    }
    15→}
    16→
    17→public final class Workspace: ObservableObject {
    18→    // MARK: - Published State
    19→
    20→    @Published public var tabs: [TabItem] = []
    21→    @Published public var selectedTabID: UUID?
    22→    @Published public var directoryURL: URL?
    23→    @Published public var fileTreeNodes: [FileTreeNode] = []
    24→    @Published public var markdownContent: String = "" {
    25→        didSet {
    26→            tocItems = extractTOC(from: markdownContent)
    27→        }
    28→    }
    29→    @Published public var tocItems: [TOCItem] = []
    30→    @Published public var errorMessage: String?
    31→
    32→    // Persisted state
    33→    @AppStorage("sidebarWidth") public var sidebarWidth: Double = 200
    34→    @AppStorage("lastOpenedFiles") private var lastOpenedFilesData: Data = Data()
    35→    @AppStorage("lastDirectory") private var lastDirectoryPath: String = ""
    36→
    37→    /// Computed property for showing error alerts
    38→    public var showError: Bool {
    39→        get { errorMessage != nil }
    40→        set { if !newValue { errorMessage = nil } }
    41→    }
    42→
    43→    /// Current file's directory for resolving relative image paths
    44→    public var currentFileDirectory: URL? {
    45→        selectedTab?.directoryURL ?? directoryURL
    46→    }
    47→
    48→    private let fileWatcher = FileWatcher()
    49→
    50→    public init() {}
    51→
    52→    // MARK: - Computed Properties
    53→
    54→    public var selectedTab: TabItem? {
    55→        guard let id = selectedTabID else { return nil }
    56→        return tabs.first { $0.id == id }
    57→    }
    58→
    59→    public var displayName: String {
    60→        if let tab = selectedTab {
    61→            if let dir = directoryURL {
    62→                return "\(tab.name) — \(dir.lastPathComponent)"
    63→            }
    64→            return tab.name
    65→        }
    66→        if let dir = directoryURL {
    67→            return dir.lastPathComponent
    68→        }
    69→        return "MDPreview"
    70→    }
    71→
    72→    // MARK: - Opening
    73→
    74→    public func openFile(_ url: URL) {
    75→        let standardized = url.standardizedFileURL
    76→
    77→        // Check if file is readable
    78→        guard FileManager.default.isReadableFile(atPath: standardized.path) else {
    79→            errorMessage = "Cannot read file: \(standardized.lastPathComponent)"
    80→            return
    81→        }
    82→
    83→        if let existing = tabs.first(where: { $0.url.standardizedFileURL == standardized }) {
    84→            selectTab(existing.id)
    85→            return
    86→        }
    87→        let tab = TabItem(url: standardized)
    88→        tabs.append(tab)
    89→        selectTab(tab.id)
    90→        saveState()
    91→    }
    92→
    93→    public func openDirectory(_ url: URL) {
    94→        directoryURL = url.standardizedFileURL
    95→        fileTreeNodes = FileTreeNode.buildTree(from: url)
    96→        lastDirectoryPath = url.path
    97→    }
    98→
    99→    public func openURL(_ url: URL) {
   100→        var isDir: ObjCBool = false
   101→        guard FileManager.default.fileExists(atPath: url.path, isDirectory: &isDir) else {
   102→            errorMessage = "File not found: \(url.lastPathComponent)"
   103→            return
   104→        }
   105→        if isDir.boolValue {
   106→            openDirectory(url)
   107→        } else {
   108→            openFile(url)
   109→        }
   110→    }
   111→
   112→    public func openFromPath(_ path: String) {
   113→        let url: URL
   114→        if path.hasPrefix("/") || path.hasPrefix("~") {
   115→            url = URL(fileURLWithPath: (path as NSString).expandingTildeInPath)
   116→        } else {
   117→            let cwd = FileManager.default.currentDirectoryPath
   118→            url = URL(fileURLWithPath: cwd).appendingPathComponent(path)
   119→        }
   120→        openURL(url)
   121→    }
   122→
   123→    // MARK: - Tabs
   124→
   125→    public func selectTab(_ id: UUID) {
   126→        guard let tab = tabs.first(where: { $0.id == id }) else { return }
   127→        selectedTabID = tab.id
   128→        loadContent(for: tab)
   129→    }
   130→
   131→    public func selectTab(at index: Int) {
