                let prefix = String(markdown[prefixRange])  // ![]( or [](
                var relativePath = String(markdown[pathRange])  // ./image.png
                let suffix = String(markdown[suffixRange])  // )

                // Remove leading ./
                if relativePath.hasPrefix("./") {
