            .replacingOccurrences(of: "\r", with: "\\n")
    }

    public final class Coordinator: NSObject, WKNavigationDelegate {
        public var webView: WKWebView?
        public var isLoaded = false
        public var pendingContent: String?
        public var baseURL: URL?

